CREATE TABLE [HotelService] (
	[ServiceId] INTEGER IDENTITY(1,1) PRIMARY KEY,
	[HotelId] INTEGER REFERENCES dbo.Hotel(HotelId),
	[Bathroom] NVARCHAR(MAX),
	[Bedroom] NVARCHAR(MAX),
	[Dinningroom] NVARCHAR(MAX),
	[Language] NVARCHAR(MAX),
	[Internet] NVARCHAR(MAX),
	[DrinkAndFood] NVARCHAR(MAX),
	[ReceptionService] NVARCHAR(MAX),
	[CleaningService] NVARCHAR(MAX),
