	[Area] FLOAT,
	[Bed] NVARCHAR(MAX),
	[SuitableFor] INTEGER,
	[Dinningroom] NVARCHAR(MAX),
	[Bathroom] NVARCHAR(MAX),
	[RoomService] NVARCHAR(MAX),
	[Smoke] BIT,
	[View] NVARCHAR(MAX),
	[Price] MONEY,
