CREATE TABLE [BookingStatus] (
	[StatusId] INTEGER IDENTITY(1,1) PRIMARY KEY,
	[StatusName] NVARCHAR(150)
)

CREATE TABLE [Booking] (
	[BookingId] INTEGER IDENTITY(1,1) PRIMARY KEY,
