CREATE TABLE [RatingStatus] (
	[StatusId] INTEGER IDENTITY(1,1) PRIMARY KEY,
	[StatusName] NVARCHAR(50)
)

CREATE TABLE [Rating] (
	[RatingId] INTEGER IDENTITY(1,1) PRIMARY KEY,
	[BookingId] INTEGER REFERENCES dbo.Booking(BookingId),
	[StarRating] INTEGER CHECK (StarRating >= 1 AND StarRating <= 5),
