--FUNCTION

CREATE FUNCTION UF_RatingPoint (@hotelId INTEGER)
RETURNS FLOAT 
AS 
BEGIN 
	DECLARE @rating FLOAT

	SELECT @rating = CAST(SUM(ra.StarRating) AS FLOAT) / COUNT(*) * 2 FROM dbo.Rating ra JOIN dbo.Booking b
	ON b.BookingId = ra.BookingId JOIN dbo.BookingDetail bd
	ON bd.BookingId = b.BookingId JOIN dbo.Room ro 
	ON ro.RoomId = bd.RoomId JOIN dbo.Hotel h
	ON h.HotelId = ro.HotelId
	WHERE h.HotelId = @hotelId AND ra.StatusId = 2
	
	RETURN @rating
END;

CREATE FUNCTION UF_CalculateRoomPrice (@hotelId INTEGER)
RETURNS FLOAT 
AS 
BEGIN 
	DECLARE @Price FLOAT

	SELECT TOP 1 @Price = ((CAST(r.Price AS FLOAT)) * (100 - CAST(r.DiscountPercent AS FLOAT))/100)  FROM dbo.Room r JOIN dbo.Hotel h
	ON h.HotelId = r.HotelId
	WHERE h.HotelId = @hotelId
	ORDER BY ((CAST(r.Price AS FLOAT)) * (100 - CAST(r.DiscountPercent AS FLOAT))/100) ASC
	
	RETURN @Price
END;